import osfrom webexteamsbot import TeamsBotfrom webexteamsbot.models import Responseimport sys_var# Enviromentbot_email = os.getenv("TEAMS_BOT_EMAIL")teams_token = os.getenv("TEAMS_BOT_TOKEN")bot_url = os.getenv("TEAMS_BOT_URL")bot_app_name = os.getenv("TEAMS_BOT_APP_NAME")# Createbot = TeamsBot(    bot_app_name,    teams_bot_token=teams_token,    teams_bot_url=bot_url,    teams_bot_email=bot_email,    debug=True)def greeting(incoming_msg):    sender = bot.teams.people.get(incoming_msg.personId)    response = Response()    response.markdown = "Hello!  "    response.markdown += "Hom nay em tuyet lam !!!"    return responsebot.set_greeting(greeting)if __name__ == "__main__":     bot.run(host="0.0.0.0")